import React, { useState } from "react";
import { useParams } from "react-router";
import { movieData } from "../../mockData/movieData";
import goods from "../../mockData/goods";


import { HeaderTop } from "../Header/Header";
import Footer from "../Footer/Footer";
import StoreModal from "../StoreModal/StoreModal";

export default function Store() {
    const { params } = useParams();
    const [idMovie, fullDate, time] = params.split("&");
    const [movie] = movieData.filter(item => item.id === +idMovie);
    const [date, month] = fullDate.split(",");
    const [isShown, setShow] = useState(false);

    console.log(movie);

    function tabHandler(num) {

    }

    function filterData() {

    }

    function getListGoods() {
        return (
            goods.map((good, index) => (
                <div className="cinema-menu__item good">
                    <div className="good__image">
                        <img src={good.image} alt="img-food" className="img-good" />
                    </div>
                    <div className="good__info info-good">
                        <div className="info-good__title">{good.title}</div>
                        <div className="info-good__price">{good.price} грн</div>
                    </div>
                    <div className="good__bottom">
                        <div className="good__size-select"></div>
                        <div className="good__count">

                        </div>
                    </div>
                </div>
            ))
        );
    }

    function getDate() {
        const monthNames = ["січня", "лютого", "березня", "квітня", "травня", "червня", "липня", "серпня", "вересня", "жовтня", "листопада", "грудня"];
        return `${date} ${monthNames[month - 1]}`;
    }

    const getTags = (tags) => {
        return (
            (tags.length > 1)
                ? tags.map((tag, index) => (<span className="tags__item" key={index}>{tag}</span>))
                : <span className="tags__item">{tags}</span>
        );
    };

    return (
        <>
            <header className="header header--details">
                <div className="header__container">
                    <HeaderTop tabs={false} page={"store"} tabHandler={tabHandler} />
                </div>
            </header>
            <main className="main main--store">
                <div className="main__container">
                    <section className="main__details details">
                        <div className="details__poster">
                            <img src={movie.src} alt="poster" className="poster-image" />
                        </div>
                        <div className="details__content details-content">
                            <div className="details-content__title">{movie.title}</div>
                            <div className="details-content__tags tags">{getTags(movie.tags)}</div>
                            <div className="details-content__columns">
                                <div className="details-content__row">
                                    <div className="details-content__col">Дата:</div>
                                    <div className="details-content__col">{getDate()}</div>
                                </div>
                                <div className="details-content__row">
                                    <div className="details-content__col">Час:</div>
                                    <div className="details-content__col">{time}</div>
                                </div>
                                <div className="details-content__row">
                                    <div className="details-content__col">Опис:</div>
                                    <div className="details-content__col">{movie.description}</div>
                                </div>
                            </div>
                        </div>
                    </section>
                    <section className="main__tabs-content tabs-content">
                        <section className="main__places places">
                            <div className="places__title">Оберіть місця</div>
                            <div className="places__variants variants">
                                <div className="variants__item">
                                    <div>SVG </div> <div>price</div>
                                </div>
                                <div className="variants__item">
                                    <div>SVG </div> <div>price</div>
                                </div>
                                <div className="variants__item">
                                    <div>SVG </div> <div>price</div>
                                </div>
                                <div className="variants__item">
                                    <div>SVG </div> <div>price</div>
                                </div>
                            </div>
                            <div className="places__map places-map">

                            </div>
                        </section>
                        <section className="main__cinema-menu cinema-menu">
                            <div className="cinema-menu__title">Кіноменю:</div>
                            <ul className="cinema-menu__list-filters">
                                <li><a href="#" onClick={filterData}>Все</a></li>
                                <li><a href="#" onClick={() => filterData("попкорн")}>Попкорн</a></li>
                                <li><a href="#" onClick={() => filterData("напої")}>Напої</a></li>
                            </ul>
                            <div className="cinema-menu__list-goods">
                                {getListGoods}
                            </div>
                        </section>
                    </section>
                    <StoreModal show={isShown} />
                </div>
            </main>
            <Footer />
        </>
    );
}
