import React, { useState } from "react";
import Footer from "../Footer/Footer";
import { Minus, Plus, NextStep } from "../Svg/Svg";

import MultipleSelect from "../../common/MultipleSelect/MultipleSelect";

import "../PersonalTab/PersonalTab.scss";

export default function PersonalTab() {
    const [openTab, setTab] = useState(true);
    const [canShow, setShow] = useState(true);
    const [isShow, setShowInput] = useState(false);

    function openChosenTab(isPersonal) {
        setTab(isPersonal);
    }

    function getSelectedOptions(optionsValue) {
        console.log(optionsValue);
    }

    function stateAddedInput(e) {
        e.preventDefault();
        setShow(!canShow ? true : false);
        setShowInput(!isShow ? true : false);
    }

    function getUserData(e) {

    }

    function getPersonalData(e) {
        e.preventDefault();
        setTab(false);
    }

    function convertInputTextToDate(e) {
        if (e.target.type === "text") {
            e.target.type = "date";
        }
        else e.target.type = "text";
    }

    return (
        <>
            <form className="form form--contact" method="POST" encType="multipart/form-data" onSubmit={getUserData}>
                <div className="contact-tab__sub-title">Як з вами зв’язатися</div>
                <label className="label">
                    <input type="text" name="user-email" placeholder="E-mail" className="input input--focus" required />
                </label>
                <label className="label">
                    <input type="text" name="user-repEmail" placeholder="Підтвердіть E-mail" className="input input--focus" required />
                </label>
                <label className="label">
                    <input type="text" name="user-tel" placeholder="Номер телефону" className="input input--focus" required />
                </label>
                <div className="contact-tab__sub-title">Встановіть пароль</div>
                <label className="label">
                    <input type="password" name="user-pass" placeholder="Пароль" className="input input--focus" required />
                </label>
                <label className="label">
                    <input type="password" name="user-repPass" placeholder="Підтвердіть пароль" className="input input--focus" required />
                </label>
                <label className="label">
                    <input type="checkbox" name="remember" className="checkbox" />
                    <span className="label-text">Я хочу отримувати розслику від “CinemaPark” </span>
                </label>
                <label className="label">
                    <input type="checkbox" name="remember" className="checkbox" />
                    <span className="label-text">Я ознайомлений(на) та приймаю правила ТОВ “CinemaPark”</span>
                </label>
                <button className="button-sub"></button>
            </form>
        </>
    );
}
