import React from "react";
import "../Slider/Slider.scss";
import { sliderData } from "../../mockData/MockData";

export default function Slider() {

    const getTags = (tags) => {
        return (
            (tags.length > 1)
                ? tags.map((tag, index) => (<span classNameName="tags__item" key={index}>{tag}</span>))
                : <span classNameName="tags__item">{tags}</span>
        );
    };

    const getPreviews = (previews) => {
        return (
            (previews.length > 1)
                ? previews.map(({ href, time, tooltip: { price, hall } }, index) => (
                    <div key={index} classNameName="previews__item" >
                        <a a href={href} classNameName="preview__link">{time}</a>
                        <div classNameName="preview__tooltip">
                            <span className="preview__price">{price}</span>
                            <span className="preview__hall">{hall}</span>
                        </div>
                    </div>))
                : (<div key={1} classNameNameName="previews__item" >
                    <a a href={previews["pr1"].href} classNameName="preview__link">{previews["pr1"].time}</a>
                    <div classNameName="preview__tooltip">
                        <span className="preview__price">{previews["pr1"].tooltip.price}</span>
                        <span className="preview__hall">{previews["pr1"].tooltip.hall}</span>
                    </div>
                </div>)
        );
    };

    const getAboutMovie = () => {
        // idMovie = 1src,
        return sliderData.map(({ tags, title, genre, previews, urlTrailer, getDetail }) => {
            return (
                <>
                    <div classNameName="slider-content__tags tags">{getTags(tags)} </div>
                    <div classNameName="slider-content__movie-title">{title}</div>
                    <div classNameName="slider-content__genre"><span classNameName="genre-title">Жанр: </span>{genre}</div>
                    <div classNameName="slider-content__previews previews">
                        <div classNameName="previews__title">Сеанси на сьогодні:</div>
                        {getPreviews(previews)}
                    </div>
                    <div slider-content__more get-more>
                        <a href={urlTrailer} classNameName="get-more__item">Трейлер</a>
                        <a href={getDetail} classNameName="get-more__item">Детальніше</a>
                    </div>
                </>
            );
        });
    };

    return (
        <div classNameName="slider">
            <div classNameName="slider__content slider-content">
                {getAboutMovie()}
            </div>
            <div classNameName="slider__slick">

            </div>
        </div>
    );
}

