import React from "react";
import "../Header/Header.scss";

import { Link } from "react-router-dom";

import Navigation from "../Navigation/Navigation";
import Slider from "../Slider/Slider";
import Login from "../Login/Login";
import Search from "../Search/Search";

export default function Header() {
    return (
        <header className="header">
            <div className="header__container">
                <HeaderTop />
                <Slider isPresale={false} />
            </div>
        </header>
    );
}

export function HeaderTop(props) {
    function getTabs() {
        return (
            <div className="header__tabs tabs-header">
                <a className="tabs-header__link" href="#personal">01 Персональні дані</a>
                <a className="tabs-header__link" href="#contacts">02 Контактні дані і пароль</a>
            </div>
        );
    }

    return (
        <div className="header__top">
            <Navigation />
            {props.tabs ? { getTabs } : ""}
            <div className="header__func-block">
                <Login />
                <Search />
            </div>
        </div>
    );
}
