import React from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import "../MultipleSelect/MultipleSelect.scss";

// const useStyles = makeStyles((theme) => ({
//     formControl: {
//         margin: theme.spacing(1),
//         minWidth: 120,
//         maxWidth: 300,
//     },
//     chips: {
//         display: 'flex',
//         flexWrap: 'wrap',
//     },
//     chip: {
//         margin: 2,
//     },
//     noLabel: {
//         marginTop: theme.spacing(3),
//     },
// }));

// const ITEM_HEIGHT = 48;
// const ITEM_PADDING_TOP = 8;
// const MenuProps = {
//     PaperProps: {
//         style: {
//             maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
//             width: 250,
//         },
//     },
// };

const names = [
    "Бойовик",
    "Вестерн",
    "Детектив",
    "Документальний фільм",
    "Драма",
    "Комедія",
    "Мелодрама",
    "Пригоди"
];

export default function MultipleSelect(props) {
    // const classes = useStyles();
    // const theme = useTheme();
    const [personName, setPersonName] = React.useState([]);

    const handleChange = (event) => {
        setPersonName(event.target.value);
    };

    const handleChangeMultiple = (event) => {
        const { options } = event.target;
        const value = [];
        for (let i = 0, l = options.length; i < l; i += 1) {
            if (options[i].selected) {
                value.push(options[i].value);
            }
        }
        setPersonName(value);

        props.selectedOptions(value);
    };
    //  className={classes.formControl}                    MenuProps={MenuProps}
    return (
        <div>
            <FormControl>
                <Select
                    labelId="demo-mutiple-name-label"
                    className="movies-kinds"
                    multiple
                    name="loved-genre"
                    value={personName || "Оберіть ваші улюблені жанри"}
                    onChange={handleChange}
                    input={<Input />}

                >
                    {names.map((name) => (
                        <MenuItem key={name} value={name} className="movies-kinds__item">
                            {name}
                        </MenuItem>
                    ))}
                </Select>
            </FormControl>
        </div>
    );
}
