export const seatsRowsSideLeft = [];
export const seatsRowsSideRight = [];
export const seatsRowsCenter = [];
let countCols = 9;
let seat, row;
for (let i = 1; i <= 13; i++) {
    seat = {};
    row = [];

    if (i > 9 && i !== 11) countCols--;

    for (let j = 1; j <= countCols; j++) {
        seat = {
            id: [i, j],
            tooltip: `${i}ряд, ${j} місце`
        };
        row.push(seat);
    }
    seatsRowsSideRight.push(row);
}

countCols = 14;
for (let i = 1; i <= 13; i++) {
    seat = {};
    row = [];

    for (let j = 1; j <= countCols; j++) {
        seat = {
            id: [i, j],
            tooltip: `${i}ряд, ${j} місце`
        };
        row.push(seat);
    }
    seatsRowsCenter.push(row);
}

countCols = 9;
let countNulls = 0;
for (let i = 1; i <= 13; i++) {
    seat = {};
    row = [];

    if (i === 10 || i === 11) {
        countNulls = 1;
        row.fill("", 0, countNulls);
    }
    else if (i >= 12) {
        countNulls++;
        row.fill("", 0, countNulls);
    }

    for (let j = 1; j <= countCols; j++) {
        seat = {
            id: [i, j],
            tooltip: `${i}ряд, ${j} місце`
        };
        row.push(seat);
    }
    seatsRowsSideLeft.push(row);
}
