import React from "react";
import "../Slider/Slider.scss";
import { sliderData } from "../../mockData/MockData";

export default function Slider() {

    const getAboutMovie = (idMovie = 1) => {

        return sliderData.map(({ src, tags, title, genre, preview, urlTrailer, getDetail }) => {
            return (
                <div className="slider-content__tags tags">
                    {(tags.length > 1)
                        ? tags.map(tag => <span className="tags__item">{tag}</span>)
                        : <span className="tags__item">{tag}</span>}
                </div>
            );
        });
    }

    return (
        <div className="slider">
            <div className="slider__content slider-content">
                <div className="slider-content__tags tags">
                    <span className="tags__item"></span>
                </div>
                <div className="slider-content__genre">Жанр: </div>
                <div className="slider-content__previews previews">Сеанси на сьогодні:
                    <span className="previews__item"></span>
                </div>
                <div slider-content__more get-more>
                    <a href="#" className="get-more__item">Трейлер</a>
                    <a href="#" className="get-more__item">Детальніше</a>
                </div>
            </div>
            <div className="slider__slick">

            </div>
        </div>
    );
}
