import React from "react";
import "../Slider/Slider.scss";
import { sliderData } from "../../mockData/MockData";

export default function Slider() {

    const getTags = (tags) => {
        return (
            (tags.length > 1)
                ? tags.map((tag, index) => (<span className="tags__item" key={index}>{tag}</span>))
                : <span className="tags__item">{tags}</span>
        );
    };

    const getPreviews = (previews) => {
        return (
            (previews.length > 1)
                ? [...previews].map(({ href, time, tooltip: { price, hall } }, index) => (
                    <div key={index} className="previews__item preview" >
                        <a a href={href} className="preview__link">{time}</a>
                        <div className="preview__tooltip">
                            <span className="preview__price">{price}</span>
                            <span className="preview__hall">{hall}</span>
                        </div>
                    </div>))
                : (<div className="previews__item preview">
                    <a a href={previews[0].href} className="preview__link">{previews[0].time}</a>
                    <div className="preview__tooltip">
                        <span className="preview__price">{previews[0].tooltip.price}</span>
                        <span className="preview__hall">{previews[0].tooltip.hall}</span>
                    </div>
                </div>)
        );
    };

    const getAboutMovie = () => {
        // idMovie = 1src,
        return sliderData.map(({ tags, title, genre, previews, urlTrailer, getDetail }) => {
            return (
                <>
                    <div className="slider-content__tags tags">{getTags(tags)} </div>
                    <div className="slider-content__movie-title">{title}</div>
                    <div className="slider-content__genre"><span className="genre-title">Жанр: </span>{genre}</div>
                    <div className="slider-content__previews previews">
                        <div className="previews__title">Сеанси на сьогодні:</div>
                        <div className="previews__items">{getPreviews(previews)}</div>
                    </div>
                    <div className="slider-content__more get-more">
                        <a href={urlTrailer} className="get-more__item">
                            <svg width="16"
                                height="19"
                                viewBox="0 0 16 19"
                                fill="none"
                                xmlns="http://www.w3.org/2000/svg" className="trailer-svg">
                                <path fillRule="evenodd" clipRule="evenodd"
                                    d="M0.253651 0.0649001C0.410405 -0.0238527 0.602782 -0.0214256 0.757248 0.0712536L15.7572 9.07125C15.9079 9.16162 16 9.32437 16 9.5C16 9.67563 15.9079 9.83838 15.7572 9.92875L0.757248 18.9287C0.602782 19.0214 0.410405 19.0239 0.253651 18.9351C0.0968963 18.8463 0 18.6801 0 18.5V0.5C0 0.319864 0.0968963 0.153653 0.253651 0.0649001ZM1 1.3831V17.6169L14.5282 9.5L1 1.3831Z"
                                    fill="white" fillOpacity="0.8" />
                            </svg>

                            Трейлер</a>
                        <a href={getDetail} className="get-more__item">Детальніше</a>
                    </div>
                </>
            );
        });
    };

    return (
        <div className="slider">
            <div className="slider__content slider-content">
                {getAboutMovie()}
            </div>
            <div className="slider__slick">

            </div>
        </div>
    );
}

