import React, { useState } from "react";
import { Exit, NextStep } from "../Svg/Svg";
import "./StoreModal.scss";
import { seatsVariants } from "../../mockData/seats";

export default function StoreModal(props) {
    const [isResized, setResized] = useState(false);
    const [isRendered, setRender] = useState(false);
    //const goods = props.goods;
    const tickets = props.tickets;
    const [fullDate, time] = props.dateTime;

    function resizeModal() {
        setResized(isResized ? false : true);
    }

    function getTypeOfSeat(type) {
        const [seat] = seatsVariants.filter(seat => seat.title === type);
        return seat.icon;
    }

    function getPriceOfSeat(type) {
        const [seat] = seatsVariants.filter(seat => seat.title === type);
        return seat.price;
    }

    function getTotalTicketPrice() {
        let total = 0;

        tickets.forEach(ticket => {
            total += getPriceOfSeat(ticket.type);
        });

        return total;
    }

    function deleteTicket(e) {
        const id = e.currentTarget.dataset.dataIdRow;
        tickets.splice(id, 1);
        setRender(true);
    }

    function getTicketsList() {
        return tickets.map((ticket, index) => (
            <tr className="tickets-table__row" key={index}>
                <td className="tickets-table__col">{getTypeOfSeat(ticket.type)}</td>
                <td className="tickets-table__col">{ticket.row}</td>
                <td className="tickets-table__col">{ticket.seat}</td>
                <td className="tickets-table__col">{fullDate}</td>
                <td className="tickets-table__col">{time}</td>
                <td className="tickets-table__col">{getPriceOfSeat(ticket.type)}</td>
                <td className="tickets-table__col">
                    <button className="tickets-table__button" data-id-row={index} onClick={deleteTicket}>
                        <Exit className="table-delete" />
                    </button>
                </td>
            </tr>
        ));
    }

    function getGoodsList() {
        return tickets.map((ticket, index) => (
            <tr className="market-table__row" key={index}>
                <td className="market-table__col">{getTypeOfSeat(ticket.type)}</td>
                <td className="market-table__col">{ticket.row}</td>
                <td className="market-table__col">{ticket.seat}</td>
                <td className="market-table__col">{fullDate}</td>
                <td className="market-table__col">{time}</td>
                <td className="market-table__col">{getPriceOfSeat(ticket.type)}</td>
                <td className="market-table__col">
                    <button className="market-table__button" data-id-row={index}>
                        <Exit className="table-delete" />
                    </button>
                </td>
            </tr>
        )
        );
    }

    return (
        <section className={"pop-up-store" + (props.show ? " pop-up-store--show" : "")
            + (isResized ? " pop-up-store--resize" : "")}>
            <div className={"pop-up-store__content" + (isResized ? " pop-up-store__content--show" : "")}>
                <button className="pop-up-store__button" onClick={resizeModal}>
                    <Exit className="modal-exit" />
                </button>
                <section className="pop-up-store__tickets tickets-sect">
                    <div className="tickets-sect__top">
                        <div className="tickets-sect__title">Квитки</div>
                        <div className="tickets-sect__price">{tickets.length}шт, {getTotalTicketPrice()}грн</div>
                    </div>
                    <table className="tickets-sect__table tickets-table">
                        <thead className="tickets-table__thead">
                            <tr className="tickets-table__thead-row">
                                <th className="tickets-table__thead-col">Тип</th>
                                <th className="tickets-table__thead-col">Ряд</th>
                                <th className="tickets-table__thead-col">Місце</th>
                                <th className="tickets-table__thead-col">Дата</th>
                                <th className="tickets-table__thead-col">Час</th>
                                <th className="tickets-table__thead-col">Вартість</th>
                                <th className="tickets-table__thead-col"></th>
                            </tr>
                        </thead>
                        <tbody className="tickets-table__rows">
                            {getTicketsList()}
                        </tbody>
                    </table>
                </section>
                <section className="pop-up-store__tickets market-sect">
                    <div className="market-sect__top">
                        <div className="market-sect__title">Кіномаркет</div>
                        <div className="market-sect__price">шт, грн</div>
                    </div>
                    <table className="market-sect__table market-table">
                        <thead className="market-table__thead">
                            <tr className="tickets-table__thead-row">
                                <th className="market-table__thead-col">Зображення</th>
                                <th className="market-table__thead-col">Назва</th>
                                <th className="market-table__thead-col">Кількість</th>
                                <th className="market-table__thead-col">Обєм</th>
                                <th className="market-table__thead-col">Вартість</th>
                                <th className="market-table__thead-col"></th>
                            </tr>
                        </thead>
                        <tbody className="market-table__rows">
                            {getGoodsList()}
                        </tbody>
                    </table>
                </section>
            </div>
            <div className="pop-up-store__bottom">
                <button className={"pop-up-store__resize-button" +
                    (!isResized) ? " pop-up-store__resize-button--show" : ""} onClick={resizeModal}>
                    <NextStep class="pop-arrow-up" />
                </button>
                <div className="pop-up-store__general-price">Всього: грн</div>
                <div className={"pop-up-store__count" + (!isResized ? " pop-up-store__count--show" : "")}>Квитки: {props.tickets.length}x</div>
                <div className={"pop-up-store__count" + (!isResized ? " pop-up-store__count--show" : "")}>Кіномаркет: x</div>
                <button className="button-next" type="submit">
                    <span className="button-text">Продовжити</span>
                    <NextStep />
                </button>
            </div>
        </section>
    );
}
