import React from "react";
import Slider from "react-slick";
import { sliderData } from "../../mockData/MockData";

export default function MovieSlider(props) {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
    };

    function showDetails(id) {

    }

    function getSlides() {
        return sliderData.map(({ src, title, id }) => (
            <a class="slide" key={id} onClick={showDetails(id)}>
                <span className="slide__id">{(id < 10 ? "0" + id : id)} </span>
                <figure className="slide__image">
                    <img className="image" src={src} />
                    <figcaption className="slide__title"> {title} </figcaption>
                </figure>
            </a>
        ));
    }

    return (
        <div>
            <Slider {...settings}>
                {getSlides}
            </Slider>
        </div>
    );
}
