import React from "react";
import Slider from "react-slick";
import { sliderData } from "../../mockData/MockData";

export default function MovieSlider(props) {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
    };

    function getSlides() {
        return sliderData.map(({ src, title, id }) => (
            <div class="slide">
                <span className="slide__id">{(id < 10 ? "0" + id : id)}
                </span>
            </div>
        ));
    }

    return (
        <div>
            <Slider {...settings}>
                {getSlides}
            </Slider>
        </div>
    );
}
