import React, { useState } from "react";
import comments from "../../mockData/comments";
import "./Comments.scss";
import { users } from "../../mockData/users";

export const Heart = ({ className }) => (
    <svg width="22" height="19" viewBox="0 0 22 19" fill="#fff" xmlns="http://www.w3.org/2000/svg"
        className={className}>
        <path d="M10.5782 3.46844L11 4.13131L11.4218 3.46844C12.3796 1.9634 14.0701 1 16 1C19.0239 1 21.5 3.47614 21.5 6.5C21.5 7.84956 20.8788 9.24594 19.878 10.6106C18.8811 11.97 17.541 13.2522 16.1834 14.363C14.8282 15.4718 13.4706 16.3977 12.4503 17.0469C11.9407 17.3712 11.5164 17.6258 11.2204 17.7988C11.1366 17.8477 11.0632 17.8901 11.001 17.9257C10.9386 17.8898 10.865 17.847 10.781 17.7975C10.4848 17.6232 10.0605 17.3669 9.55082 17.0406C8.53042 16.3874 7.17254 15.4567 5.81714 14.3447C4.45927 13.2306 3.11886 11.9466 2.12172 10.5892C1.12048 9.22611 0.5 7.83624 0.5 6.5C0.5 3.47614 2.97614 1 6 1C7.92993 1 9.62042 1.9634 10.5782 3.46844Z" stroke="white" strokeOpacity="0.8" />
    </svg>
);


export default function Comments() {
    const [comment, setComment] = useState();
    const [countComments, setCount] = useState(comments.length);
    const [likes, setLikes] = useState(0);
    const { name, email } = users.filter(user => user.email === localStorage.getItem("email"));
    const [user, setUser] = useState();

    function onChangeHadler(e) {
        setComment(e.target.value);
    }

    function addComment() {

        console.log(users);
        comments.push(
            {
                author: name,
                text: comment,
                countLikes: 0,
                date: new Date().toLocaleDateString('ru-RU')
            }
        );

        setCount(comments.length);
    }

    function putLike(e) {
        console.log(e);
        let ind = +e.target.parentNode.parentNode.dataset.id;
        comments[ind].countLikes = +comments[ind].countLikes + 1;

        setLikes(comments[ind].countLikes);


    }

    function getComment(author, date, text, countLikes, index) {
        return (
            <div className="comment" key={index}>
                <div className="comment__top">
                    <div className="comment__author">{author}</div>
                    <div className="comment__date">{date}</div>
                </div>
                <p className="comment__text">{text}</p>
                <div className="comment__bottom">
                    <button onClick={putLike} className="button button--like" data-id={index}>
                        <Heart className="heart-svg" />
                    </button>
                    {countLikes}
                </div>
            </div>
        );
    }


    return (
        <div className="comments">
            <div className="comments__container">
                <div className="comments__count">Коментарі ({countComments})</div>
                <div className="comments__input">
                    <textarea required className="input input--comment" onChange={onChangeHadler}
                        placeholder="Напишіть свої враження від  перегляду фільму" />
                    <button className="button button--comment" onClick={addComment}>Відправити</button>
                </div>
                <div className="comments__output">
                    <div className="comments__old">
                        {
                            comments.map((comment, index) => getComment(comment.author, comment.date, comment.text, comment.countLikes, index))
                        }
                    </div>
                </div>

            </div>
        </div>
    );
}
