import React, { useState } from "react";
import { Link } from "react-router-dom";

import { HeaderTop } from "../Header/Header";
import Footer from "../Footer/Footer";
import { Minus, Plus, NextStep } from "../Svg/Svg";

import MultipleSelect from "../../common/MultipleSelect/MultipleSelect";

import "../Registration/Registration.scss";

export default function Registration() {
    const [openTab, setTab] = useState(true);
    const [canShow, setShow] = useState(true);
    const [isShow, setShowInput] = useState(false);

    function openChosenTab(isPersonal) {
        setTab(isPersonal);
    }

    function getSelectedOptions() {

    }

    function stateAddedInput(e) {
        e.preventDefault();
        setShow(!canShow ? true : false);
        setShowInput(!isShow ? true : false);
    }

    function getUserData(e) {

    }

    function convertInputTextToDate(e) {
        if (e.target.type === "text") {
            e.target.type = "date";
        }
        else e.target.type = "text";
    }

    return (
        <>
            <header className="header header--regist">
                <div className="header__container">
                    <HeaderTop tabs="true" openTab={openChosenTab} />
                </div>
                <div className="header__history history">
                    <Link className="history__link" to="/">Головна</Link> <span className="separator">/</span>
                    <Link className="history__link" to="/">Реєстрація</Link>
                </div>
            </header>
            <main className="main">
                <div className="main__registration registration">
                    <section className={"registration__personal personal-tab" + (openTab ? " personal--show" : "")}>
                        <div className="personal-tab__title">Розкажіть нам про себе</div>
                        <form className="form form--personal" method="POST" encType="multipart/form-data" onSubmit={getUserData}>
                            <input type="file" className="upload-file" name="user-photo" />
                            <label className="label">
                                <input type="text" name="user-surname" placeholder="Прізвище" className="input input--focus" required />
                            </label>
                            <label className="label">
                                <input type="text" name="user-name" placeholder="Ім’я" className="input input--focus" required />
                            </label>
                            <label className="label">
                                <input type="text" name="user-login" placeholder="Ім’я користувача" className="input input--focus" required />
                            </label>
                            <label className="label">
                                <input type="text" onFocus={convertInputTextToDate} onBlur={convertInputTextToDate} name="user-birthday" placeholder="Дата народження" className="input input--focus" required />
                                <button className={"button-date" + (canShow ? " button-date--show" : "")} onClick={stateAddedInput}><Plus /></button>
                            </label>
                            <label className={"label label-date" + (isShow ? " label-date--show" : "")}>
                                <input type="text" onFocus={convertInputTextToDate} onBlur={convertInputTextToDate} name="closed-person-birthday" placeholder="Дата народження близької людини" className="input input--focus" required />
                                <button className="button-date button-date--show" onClick={stateAddedInput}><Minus /></button>
                            </label>
                            <label className="label">
                                <span className="label-text">Ваша стать</span>
                                <div className="radio-list">
                                    <div className="radio">
                                        <label className="label">
                                            <input type="radio" name="gender" className="radio__btn" required />
                                            <span className="radio-text">Чоловіча</span>
                                        </label>
                                    </div>
                                    <div className="radio">
                                        <label className="label">
                                            <input type="radio" name="gender" className="radio__btn" required />
                                            <span className="radio-text">Жіноча</span>
                                        </label>
                                    </div>
                                </div>


                            </label>
                            <label className="label">
                                <MultipleSelect selectedOptions={getSelectedOptions} />
                                {/* <select name="loved-genre" multiple>
                                    <option value="0" selected>Оберіть ваші улюблені жанри</option>
                                    <option value="Бойовик">Бойовик</option>
                                    <option value="Вестерн">Вестерн</option>
                                    <option value="Детектив">Детектив</option>
                                    <option value="Документальний фільм">Документальний фільм</option>
                                    <option value="Драма">Драма</option>
                                    <option value="Комедія">Комедія</option>
                                    <option value="Мелодрама">Мелодрама</option>
                                    <option value="Пригоди">Пригоди</option>
                                </select> */}
                            </label>
                            <button className="button-next" onClick={() => setTab(false)}>Продовжити <NextStep /></button>
                        </form>
                    </section>
                    <section className={"registration__contact contact-tab" + (!openTab ? " contact--show" : "")}>
                        Контакты
                    </section>
                </div>
            </main>
            <Footer />
        </>
    );
}
