import React, { useState } from "react";
import "../Header/Header.scss";

import Navigation from "../Navigation/Navigation";
import Slider from "../Slider/Slider";
import Login from "../Login/Login";
import Search from "../Search/Search";

export default function Header() {
    return (
        <header className="header">
            <div className="header__container">
                <HeaderTop />
                <Slider isPresale={false} />
            </div>
        </header>
    );
}

export function HeaderTop(props) {
    const [isActive, setActive] = useState(true);
    //props.curTab = false
    function openTab(isPersonal) {
        props.openTab(isPersonal);
        setActive(isPersonal);
    }

    function getTabs() {
        console.log(isActive && !props.currentTab);
        console.log(!isActive && !props.currentTab);
        return (
            <div className="header__tabs tabs-header">
                <a className={"tabs-header__link" + (isActive && props.currentTab ? " tabs-header__link--active" : "")} onClick={() => openTab(true)}>01 Персональні дані</a>
                <a className={"tabs-header__link" + (!isActive && props.currentTab ? " tabs-header__link--active" : "")} onClick={() => openTab(false)}>02 Контактні дані і пароль</a>
            </div>
        );
    }

    return (
        <div className="header__top">
            <Navigation />
            {props.tabs ? getTabs() : ""}
            <div className="header__func-block">
                <Login />
                <Search />
            </div>
        </div>
    );
}
