import React, { useState } from "react";
import { Link } from "react-router-dom";

import "../Header/Header.scss";

import Navigation from "../Navigation/Navigation";
import Slider from "../Slider/Slider";
import Login from "../Login/Login";
import Search from "../Search/Search";

export default function Header() {
    return (
        <header className="header">
            <div className="header__container">
                <HeaderTop />
                <Slider isPresale={false} />
            </div>
        </header>
    );
}

export function HeaderTop(props) {
    const [isPersonal, setPersonal] = useState(true);

    function openRegistrationTab(isPersonal) {
        props.openTab(isPersonal);
        setPersonal(isPersonal);
    }

    console.log(props.page);

    function getTabsByPage(page) {


        return (
            (page === "registration")
                ? getTabsOfRegistration()
                : getTabsOfStore()
        );
    }


    function getTabsOfStore() {
        return (
            <div className="header__tabs tabs-header">
                <a className={"tabs-header__link" + (isPersonal ? " tabs-header__link--active" : "")} onClick={() => openRegistrationTab(true)}>01 Обрати місце</a>
                <a className={"tabs-header__link" + (!isPersonal ? " tabs-header__link--active" : "")} onClick={() => openRegistrationTab(false)}>02 Магазин</a>
                <a className={"tabs-header__link" + (!isPersonal ? " tabs-header__link--active" : "")} onClick={() => openRegistrationTab(false)}>03 Оплата</a>
            </div>
        );
    }

    function getTabsOfRegistration() {
        return (
            <div className="header__tabs tabs-header">
                <a className={"tabs-header__link" + (isPersonal ? " tabs-header__link--active" : "")} onClick={() => openRegistrationTab(true)}>01 Персональні дані</a>
                <a className={"tabs-header__link" + (!isPersonal ? " tabs-header__link--active" : "")} onClick={() => openRegistrationTab(false)}>02 Контактні дані і пароль</a>
            </div>
        );
    }

    return (
        <div className="header__top">
            <Navigation />
            {props.page ? getTabsByPage(props.page) : ""}
            <div className="header__func-block">
                <Login />
                <Search />
            </div>
        </div>
    );
}
