import React from "react";
import Slider from "react-slick";
import { sliderData } from "../../mockData/MockData";

import "../../styles/slick.scss";
import "../../styles/slick-theme.scss";
import "../Slider/Slider.scss";

export default function MovieSlider(props) {
    const settings = {
        infinite: true,
        speed: 800,
        slidesToShow: 3,
        slidesToScroll: 1,
        useTransform: false,
        variableWidth: true
    };

    function addEll(e) {
        console.log('add');
    }

    function showDetails(idMovie) {
        props.sliderContent(idMovie);
    }

    function getSlides() {
        return sliderData.map(({ src, title, id }) => (
            <a className="slide" key={id} onClick={showDetails(id)}>
                <span className="slide__id">{(id < 10 ? "0" + id : id)} </span>
                <figure className="slide__image">
                    <img className="image" src={src} />
                    <figcaption className="slide__title"> {title} </figcaption>
                </figure>
            </a>
        ));
    }

    return (
        <Slider {...settings}>
            {getSlides()}
        </Slider>
    );
}
