import React from "react";

import "../MovieList/MovieList.scss";
import { movieData as movies } from "../../mockData/movieData";
import { HalfStar, Star } from "../Svg/Svg";
export default function MovieList(props) {


    function showList() {
        const [date, month] = props.dateForList.split(",");
        const filteredData = movies.filter(movie => {
            let [dateShow, monthShow] = movie.datesShow;
            console.log(dateShow, date, month);
            console.log(dateShow === date && monthShow === month);
            return (dateShow === date && monthShow === month);
        });

        console.log(filteredData);
        return (
            <section className="movie-list">
                <div className="movie-list__item movie">
                    <div className="movie__poster poster">
                        <div className="poster__tags tags">
                            <span className="tags__item"></span>
                        </div>
                        <div className="poster__title"></div>
                        <div className="poster__genre"><span className="genre-title">Жанр: </span>genre</div>
                        <div className="poster__raiting">svg</div>
                    </div>
                    <div className="poster__previews previews">
                        poster
                    </div>
                </div>
            </section>
        );
    }

    return (
        <>
            {showList()}
        </>
    );
}
