import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import "./Store.scss";
import StoreModal from "../StoreModal/StoreModal";
import { movieData } from "../../mockData/movieData";
import { HeaderTop } from "../Header/Header";
import Footer from "../Footer/Footer";
import Products from "../Products/Products";
import MapSeats from "../MapSeats/MapSeats";
import Payment from "../Payment/Payment";


const getTags = (tags) => {
    return (
        (tags.length > 1)
            ? tags.map((tag, index) => (<span className="tags__item" key={index}>{tag}</span>))
            : <span className="tags__item">{tags}</span>
    );
};

export default class Store extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            tickets: [],
            isShown: true,
            numTab: 1,
            products: []
        };
        //this.;
        this.date = "0";
        this.month = 0;
        // [date, month] = fullDate.split(",");
    }

    // const { params } = useParams();
    // const [idMovie, fullDate, time] = params.split("&");
    // const [movie] = movieData.filter(item => item.id === +idMovie);
    // const [date, month] = fullDate.split(",");
    // const [tickets, setTicketsData] = useState([]);
    // const [products, setProductsData] = useState([]);
    // const [numTab, setOpen] = useState(1);
    // const [isShown, setShow] = useState(true);

    getTickets(tickets) {
        this.setState({ tickets: tickets });
    }

    updateTickets(tickets) {
        setTicketsData(tickets);
    }

    updateProducts(products) {
        setProductsData(products);
    }

    getProducts(products) {
        setProductsData(products);
    }

    tabHandler(num) {
        setOpen(num);
    }


    getDate() {
        const monthNames = ["січня", "лютого", "березня", "квітня", "травня", "червня", "липня", "серпня", "вересня", "жовтня", "листопада", "грудня"];
        return `${date} ${monthNames[month - 1]}`;
    }
    render() {
        return (
            <>
                <header className="header header--details">
                    <div className="header__container">
                        <HeaderTop tabs={false} page={"store"} tabHandler={this.tabHandler} />
                    </div>
                </header>
                <main className="main main--store">
                    <div className="main__container">
                        {/* <section className="main__details details details--store">
                            <div className="details__poster">
                                <img src={movie.src} alt="poster" className="poster-image" />
                            </div>
                            <div className="details__content details-content">
                                <div className="details-content__title">{movie.title}</div>
                                <div className="details-content__tags tags">{getTags(movie.tags)}</div>
                                <div className="details-content__columns">
                                    <div className="details-content__row">
                                        <div className="details-content__col">Дата:</div>
                                        <div className="details-content__col">{getDate()}</div>
                                    </div>
                                    <div className="details-content__row">
                                        <div className="details-content__col">Час:</div>
                                        <div className="details-content__col">{time}</div>
                                    </div>
                                    <div className="details-content__row">
                                        <div className="details-content__col">Опис:</div>
                                        <div className="details-content__col">{movie.description}</div>
                                    </div>
                                </div>
                            </div>
                        </section> */}
                        <section className="main__tabs-content tabs-content">
                            {
                                this.state.numTab === 1 ? <MapSeats getTickets={this.getTickets.bind(this)} />
                                    : this.state.numTab === 2 ? <Products getProducts={this.getProducts} />
                                        : <Payment />
                            }

                        </section>
                        <StoreModal
                            show={this.state.isShown}
                            tickets={this.state.tickets}
                            dateTime={[0, this.time]}
                            updateTickets={this.updateTickets.bind(this)}
                            updateProducts={this.updateProducts.bind(this)}
                            products={this.state.products}
                            openTab={this.tabHandler}
                            currentTab={this.state.numTab} />
                    </div>
                </main>
                <Footer />
            </>
        );
    }

}
