import React, { useState } from "react";

export const comments = [
    {
        author: "Марина",
        text: "Дуже крутий фільм!!!",
        countLikes: 0,
        date: new Date()
    },
    {
        author: "Олег",
        text: "Я в захваті. Буду обов'язково дивитись ще!)",
        countLikes: 100,
        date: new Date()
    }
];

