    }

    const Comment = ({ author, date, text, countLikes }) => (
        <div className="comment">
            <div className="comment__top">
                <div className="comment__author">{author}</div>
                <div className="comment__date">{date}</div>
            </div>
            <p className="comment__text">{text}</p>
            <div className="comment__bottom">
                <button onClick={addComment}>Відреагувати</button>
                <button onClick={putLike}>{countLikes}</button>
            </div>
        </div>
    );


    return (
        <div className="comments">
            <div className="comments__count"></div>
            <div className="comments__input">
                <input type="text" className="input input--comments" onChange={onChangeHadler} />
                <button className="button button--comment" onClick={addComment}>Надіслати</button>
            </div>

            <div className="comments__output">
                <div className="comments__old">
                    {
                        comments.map(comment, index => (
                            <Comment
                                key={index}
                                text={comment.text}
                                date={comment.date}
                                author={comment.author}
                                countLikes={comment.countLikes}
                            />
                        ))
                    }
                </div>
            </div>
        </div>
    );
}
