import React, { useState } from "react";
import "../Navigation/Navigation.scss";

import { Youtube, Insta, Fb } from "../Svg/Svg";
import { NavLink } from "react-router";
import Logo from "../Logo/Logo";
import Hamburger from "../Hamburger/Hamburger";
import SubList from "../SubList/SubList";

export default function Navigation() {
    const [isOpen, setState] = useState('');
    const [isOpenSubList, openList] = useState(false);

    function openSubList() {
        openList(!isOpenSubList ? true : false);
    }

    function openMenu(isOpen) {
        setState(isOpen);
    }

    return (
        <nav className={"navigation" + (isOpen ? " navigation--open-menu" : "")}>
            <div className="navigation__top">
                <Hamburger openMenu={openMenu} />
                <Logo />
            </div>
            <ul className="navigation__list">
                <li className="navigation__item">
                    <NavLink to="/account" className="navigation__link">Мій профіль</NavLink>
                </li>
                <li className="navigation__item">
                    <NavLink to="/news" className="navigation__link">Новини</NavLink>
                </li>
                <li className="navigation__item">
                    <NavLink to="/store" className="navigation__link">Кіномаркет</NavLink>
                </li>
                <li className="navigation__item">
                    <NavLink to="/discounts" className="navigation__link">Акції та знижки</NavLink>
                </li>
                <li
                    className={"navigation__item navigation__item--list" +
                        (isOpenSubList ? " navigation__item--open-list" : "")}
                >
                    <NavLink
                        className="navigation__link"
                        onClick={() => openSubList()}> Наші проекти</NavLink>
                    <SubList />

                </li>
                <li className="navigation__item">
                    <NavLink to="/about" className="navigation__link">Про нас</NavLink>
                </li>
                <li className="navigation__item">
                    <NavLink to="/contacts" className="navigation__link">Контакти</NavLink>
                </li>
            </ul>
            <div className="navigation__social-networks social-networks">
                <NavLink to="https://www.instagram.com/" className="social-networks__item">
                    <Insta className={"insta social-networks__svg"} />
                </NavLink>
                <NavLink to="https://uk-ua.facebook.com/" className="social-networks__item">
                    <Fb className={"fb social-networks__svg"} />
                </NavLink>
                <NavLink to="https://www.youtube.com/" className="social-networks__item">
                    <Youtube className={"youtube social-networks__svg"} />
                </NavLink>
            </div>
            <div className="navigation__langs langs">
                <NavLink to="/ua" className="langs__link">Укр</NavLink>
                <NavLink to="/ru" className="langs__link">Рус</NavLink>
                <NavLink to="/en" className="langs__link">Eng</NavLink>
            </div>
        </nav>
    );
}
