import React, { useState } from "react";
import ReactTooltip from 'react-tooltip';
import "./MapSeats.scss";
import { Seat, BigSeat } from "../Svg/Svg";

import { seatsRowsSideLeft, seatsRowsSideRight, seatsRowsCenter } from "../../mockData/seats";


const seatsVariants = [
    {
        icon: <Seat className={" seat seat--cheaper"} />,
        price: 80
    },
    {
        icon: <Seat className={" seat seat--less-cheaper"} />,
        price: 90
    },

    {
        icon: <Seat className={" seat seat--less-expensive"} />,
        price: 120
    },
    {
        icon: <BigSeat className={" seat seat--expensive"} />,
        price: 330
    },
];

export default function MapSeats() {
    const [isChosen, setChosen] = useState(false);
    const [loading, setLoading] = useState(false);

    function getVariants() {
        return (
            <div className="variants">
                {
                    seatsVariants.map((place, index) => (
                        <div className="variants__item" key={index}>
                            <div className="variants__item-icon seat-svg">{place.icon} </div>
                            <span className="variants__item-price">{place.price} грн</span>
                        </div>
                    ))
                }
            </div>
        );
    }

    console.log(seatsRowsSideLeft);
    console.log(seatsRowsSideLeft);
    console.log(seatsRowsSideLeft);

    function addSeat() {

    }

    function removeSeat() {

    }



    return (
        <section className="seats">
            <div className="seats__title">Оберіть місця</div>
            <div className="seats__map seats-map">
                {getVariants()}
                <div className="seats-map__top">
                    <span className="seats-map__col-name">Ряд</span>
                    <span className="seats-map__col-name">Екран</span>
                    <span className="seats-map__col-name">Ряд</span>
                </div>
                <div className="seats-map__content">
                    <div className="seats-map__col">
                        {
                            seatsRowsSideLeft.map((row, index) => (
                                <div className="seats-map__row" key={index}>
                                    {
                                        row.map(({ tooltip, id, seat }, index) => (
                                            <button
                                                className="seat"
                                                key={index}
                                                data-tip={tooltip} data-id={id} onClick={addSeat}>
                                                {seat}
                                            </button>
                                        ))
                                    }
                                </div>
                            ))
                        }
                    </div>
                    <div className="seats-map__center">
                        {
                            seatsRowsCenter.map(({ tooltip, id, seat }, index) => (
                                <button
                                    className="seat"
                                    key={index}
                                    data-tip={tooltip} data-id={id} onClick={addSeat}>
                                    {seat}
                                </button>
                            ))}
                    </div>
                    <div className="seats-map__col">
                        {
                            seatsRowsSideRight.map(({ tooltip, id, seat }, index) => (
                                <button
                                    className="seat"
                                    key={index}
                                    data-tip={tooltip} data-id={id} onClick={addSeat}>
                                    {seat}
                                </button>
                            ))}
                    </div>

                </div>

            </div>
        </section>
    );
}
