import React from "react";
import Slider from "react-slick";

import "../DaysSlider/DaysSlider.scss";
import "./slick.scss";
import "./slick-theme.scss";

export default function DaysSlider() {
    const settings = {
        infinite: true,
        speed: 600,
        slidesToShow: 12,
        slidesToScroll: 1,
        useTransform: false,
        variableWidth: true
    };

    function formDaysList() {
        const date = new Date();
        let currentDate = date.getDate();
        let currentDay = date.getDay();
        let countDays = new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
        const days = ["Нд", "Пн", "Вт", "Ср", "Чт", "Пт", "Сб"];
        const dateList = [];
        let key = 0;
        let flag = true;

        for (let i = currentDate; i <= countDays; i++) {
            if (currentDay === 6) currentDay = 0;

            dateList.push(getDay(i, days[currentDay], key));
            if (countDays === i && flag) {
                countDays = currentDate;
                i = 0;
                flag = false;
            }
            currentDay++;
            key++;
        }

        return dateList;
    }

    function getDay(date, day, key) {

        return (
            <button className="day" key={key}>
                <span className="day__date">{date}</span>
                <span className="day__name">{day}</span>
            </button>
        );
    }


    return (
        <>
            <Slider Slider {...settings}>
                <div className="days__title"></div>
                <section className="movies-day__days">
                    {formDaysList().map(day => day)}
                </section>
            </Slider>
        </>
    );
}//
