import React, { useState, useEffect } from "react";
import { Login as LoginSvg, Exit, Google, Facebook, Apple, CloseEye, OpenEye } from "../Svg/Svg";
import { users } from "../../mockData/users";

import "../Login/Login.scss";

export default function Login() {
    const [isOpen, setOpen] = useState(false);
    const [canFix, fixState] = useState(false);
    const [showPass, showState] = useState(false);
    const [showEye, eyeState] = useState(false);
    let target;

    function userLogin(e) {
        e.preventDefault();
        const userEmail = e.target[0].value;
        const userPass = e.target[1].value;
        const isChecked = e.target[3].checked;
        console.log(isChecked);
        const result = users.filter(({ email, password }) => email === userEmail && password === userPass);
        if (!result) {
            alert("На жаль Ви не є членом нашого кіноклубу!");
            return;
        }

        isChecked
            ? localStorage.setItem("user", userEmail)
            : sessionStorage.setItem("user", userEmail);
    }

    function changeModalState() {
        setOpen(isOpen ? false : true);
    }

    useEffect(() => {
        isOpen ? document.body.style.overflow = "hidden" :
            document.body.style.overflow = "auto";
    });

    function fixPlaceHolder() {
        fixState(true);
    }

    function changeStatePass() {
        showState(showPass ? false : true);

        if (target) target.type = "text";
    }


    function changeStateEye(e) {
        target = !showEye ? e.target : 0;
        eyeState(showEye ? false : true);
    }

    return (
        <>
            <button className="header__login header-login" onClick={changeModalState}>
                <LoginSvg className={"header__svg"} />
            </button>
            <LoginModal isOpen={isOpen} />
        </>
    );
}
