import React from 'react';

import Input from '@material-ui/core/Input';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import "../MultipleSelect/MultipleSelect.scss";
import InputLabel from '@material-ui/core/InputLabel';
const names = [
    "Бойовик",
    "Вестерн",
    "Детектив",
    "Документальний фільм",
    "Драма",
    "Комедія",
    "Мелодрама",
    "Пригоди"
];

export default function MultipleSelect(props) {
    const [personName, setPersonName] = React.useState(["Оберіть ваші улюблені жанри"]);
    const val = ["Оберіть ваші улюблені жанри"];
    const handleChange = (event) => {
        setPersonName(event.target.value);
        props.selectedOptions(event.target.value);
    };

    const handleChangeMultiple = (event) => {
        const { options } = event.target;
        const value = [];
        for (let i = 0, l = options.length; i < l; i += 1) {
            if (options[i].selected) {
                value.push(options[i].value);
            }
        }
        setPersonName(value);

        props.selectedOptions(value);
    };

    return (
        <FormControl>
            <InputLabel id="demo-mutiple-name-label">беріть-ваші-улюблені-жанри</InputLabel>
            <Select
                className="movies-kinds"
                multiple
                name="loved-genre"
                value={val}
                onChange={handleChange}
                input={<Input />}>
                {names.map((name) => (
                    <MenuItem key={name} value={name} className="movies-kinds__item">
                        {name}
                    </MenuItem>
                ))}
            </Select>
        </FormControl>
    );
}
