import React from "react";
import goods from "../../mockData/goods";
import "./MapPlaces.scss";

const places = [
    {
        icon: "",
        price: 80
    },
    {
        icon: "",
        price: 90
    },

    {
        icon: "",
        price: 120
    },
    {
        icon: "",
        price: 330
    },
];

export default function MapSeats() {

    function getVariants() {
        return (
            <div className="variants">
                {
                    places.map((place, index) => (
                        <div className="variants__item" key={index}>
                            <div className="variants__item-icon place-svg">{place.icon} </div>
                            <div className="variants__item-price">{place.price} грн</div>
                        </div>
                    ))
                }
            </div>
        );
    }

    return (
        <section className="places">
            <div className="places__title">Оберіть місця</div>

            <div className="places__map places-map">
                <div className="places-map__top">
                    <span className="places-map__col-name">Ряд</span>
                    <span className="places-map__col-name">Екран</span>
                    <span className="places-map__col-name">Ряд</span>
                </div>
                <div className="places-map__col"></div>
                <div className="places-map__center"></div>
                <div className="places-map__col"></div>
            </div>
        </section>
    );
}
