import React, { useState } from "react";
import { Link } from "react-router-dom";

import { HeaderTop } from "../Header/Header";
import Footer from "../Footer/Footer";
import { Minus, Plus, NextStep } from "../Svg/Svg";

import MultipleSelect from "../../common/MultipleSelect/MultipleSelect";
import PersonalTab from "../PersonalTab/PersonalTab";

import "../Registration/Registration.scss";

export default function Registration() {
    const [openTab, setTab] = useState(true);
    const [canShow, setShow] = useState(true);
    const [isShow, setShowInput] = useState(false);

    function openChosenTab(isPersonal) {
        setTab(isPersonal);
    }

    function getSelectedOptions(optionsValue) {
        console.log(optionsValue);
    }

    function stateAddedInput(e) {
        e.preventDefault();
        setShow(!canShow ? true : false);
        setShowInput(!isShow ? true : false);
    }

    function getUserData(e) {

    }

    function getPersonalData(e) {
        e.preventDefault();
        setTab(false);
    }

    function convertInputTextToDate(e) {
        if (e.target.type === "text") {
            e.target.type = "date";
        }
        else e.target.type = "text";
    }

    return (
        <>
            <header className="header header--regist">
                <div className="header__container">
                    <HeaderTop tabs="true" openTab={openChosenTab} />
                </div>
                <div className="header__history history">
                    <Link className="history__link" to="/">Головна</Link> <span className="separator">/</span>
                    <Link className="history__link" to="/">Реєстрація</Link>
                </div>
            </header>
            <main className="main">
                <div className="main__registration registration">
                    <section className={"registration__personal personal-tab" + (openTab ? " personal--show" : "")}>
                        <PersonalTab />
                    </section>
                    <section className={"registration__contact contact-tab" + (!openTab ? " contact--show" : "")}>
                        Контакты
                    </section>
                </div>
            </main>
            <Footer />
        </>
    );
}
