import React, { useState } from "react";
import { useParams } from "react-router-dom";

import { Trailer } from "../Svg/Svg";
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import { movieData } from "../../mockData/movieData";

import "../Details/Details.scss";


export default function Details() {
    const { idMovie } = useParams();

    function getMovieDetails() {
        const movie = movieData.map(item => item.id = idMovie);

        return (
            <>
                <div className="details__poster" style={{ backgroundImage: `url(${movie.src})` }}></div>
                <div className="details__content details-content">
                    <div className="details-content__title"></div>
                    <div className="details-content__rating"></div>
                    <div className="details-content__tags tags"></div>
                    <div className="details-content__row">


                    </div>
                </div>
            </>

        );
    }

    return (
        <>
            <Header />
            <main className="main">
                <div className="main__container">
                    <section className="main__details details">

                    </section>
                </div>
            </main>
            <Footer />
        </>
    );
}
