import React, { useState } from "react";
import "../Slider/Slider.scss";
import { movieData as sliderData } from "../../mockData/movieData";
import { Trailer, GetMore } from "../Svg/Svg";
import MovieSlider from "../MovieSlider/MovieSlider";
import { Link } from "react-router-dom";


let count = 1;

export default function Slider(props) {
    const flag = props.isPresale;
    const filteredData = getFilterData(flag);
    const [movieId, setMovieId] = useState(filteredData[0].id);


    function showSlide(flagButton) {
        console.log(flagButton, count, filteredData);
        if (flagButton && count !== filteredData.length) {
            count++;
            getAboutMovie(count);
        }
        else if (!flagButton && count !== 1) {
            count--;
            getAboutMovie(count);
        }


    }


    function getFilterData(flag) {
        return (
            !flag
                ? sliderData.filter(movie => movie.tags[0] !== "PRESALE")
                : sliderData.filter(movie => movie.tags[0] === "PRESALE")
        );
    }

    function getTags(tags) {
        return (
            (tags.length > 1)
                ? tags.map((tag, index) => (
                    tag === "PRESALE"
                        ? <span className="tags__item tags__item--presale" key={index}>{tag}</span>
                        : <span className="tags__item" key={index}>{tag}</span>
                ))
                : tags[0] === "PRESALE"
                    ? <span className="tags__item tags__item--presale">{tags}</span>
                    : <span className="tags__item">{tags}</span>
        );
    }

    function getPreviews(previews) {
        return (
            (previews.length > 1)
                ? [...previews].map(({ href, time, tooltip: { price, hall } }, index) => (
                    <div key={index} className="previews__item preview" >
                        <a a href={href} className="preview__link">{time}</a>
                        <div className="preview__tooltip">
                            <span className="preview__price">{price}</span>
                            <span className="preview__hall">{hall}</span>
                        </div>
                    </div>))
                : (<div className="previews__item preview">
                    <a a href={previews[0].href} className="preview__link">{previews[0].time}</a>
                    <div className="preview__tooltip">
                        <span className="preview__price">{previews[0].tooltip.price}</span>
                        <span className="preview__hall">{previews[0].tooltip.hall}</span>
                    </div>
                </div>)
        );
    }

    function getMore(urlTrailer, getDetail) {
        return (
            <div className="slider-content__more get-more">
                <a href={urlTrailer} className="get-more__item" target="__blank">
                    <Trailer className={"get-more__svg"} />
                    <span className="get-more__title">Трейлер</span>
                </a>
                <Link to={`/details/${getDetail}`} className="get-more__item">
                    <GetMore className={"get-more__svg"} />
                    <span className="get-more__title">Детальніше</span>
                </Link>
            </div>
        );
    }

    function getSubTitle(datesShow, flag) {
        const month = ["січня", "лютого", "березня", "квітня", "травня", "червня", "липня", "серпня", "вересня", "жовтня", "листопада", "грудня"];
        return (
            !flag ? <div className="previews__title">Сеанси на сьогодні:</div>
                : <div className="previews__title">В кіно з {datesShow[0][0] + " " + month[datesShow[0][1] - 1]}:</div>
        );
    }

    function getAboutMovie(idMovie) {
        !idMovie ? movieId : setMovieId(idMovie);
        const { src, tags, title, genre, previews, urlTrailer, getDetail, datesShow } = sliderData[movieId - 1];

        return (
            <div className="slider" style={{ backgroundImage: `url('${src}')` }}>
                <div className="slider__content slider-content">
                    {flag ? <div className="slider-content__title">Скоро в кіно</div> : ""}
                    <div className="slider-content__tags tags">{getTags(tags)} </div>
                    <div className="slider-content__movie-title">{title}</div>
                    <div className="slider-content__genre"><span className="genre-title">Жанр: </span>{genre}</div>
                    <div className="slider-content__previews previews">
                        {getSubTitle(datesShow, flag)}
                        <div className="previews__items">{getPreviews(previews)}</div>
                    </div>
                    {getMore(urlTrailer, getDetail)}
                </div>
            </div>
        );
    }

    return (
        <>
            {getAboutMovie()}
            <MovieSlider getAboutMovie={getAboutMovie} filterData={filteredData} />
            <button className="slick-arrow slick-prev" onClick={() => showSlide(false)}></button>,
            <button className="slick-arrow slick-next" onClick={() => showSlide(true)}></button>,
        </>
    );
}
