import React from "react";
import Slider from "react-slick";

import "../../styles/slick.scss";
import "../../styles/slick-theme.scss";

export default function DaysSlider() {
    const settings = {
        infinite: true,
        speed: 600,
        slidesToShow: 12,
        slidesToScroll: 1,
        useTransform: false,
        variableWidth: true
    };

    function formDaysList() {
        const date = new Date();
        let currentDate = date.getDate();
        let currentDay = date.getDay();
        let countDays = new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
        const days = ["Нд", "Пн", "Вт", "Ср", "Чт", "Пт", "Сб"];
        const dateList = [];
        let key = 0;
        let flag = true;

        console.log(currentDate, countDays);
        for (let i = currentDate; i <= countDays; i++) {
            if (currentDay === 6) currentDay = 0;

            dateList.push(getDay(i, days[currentDay], key));
            if (countDays === i && flag) {
                countDays = currentDate;
                i = 0;
                flag = false;
            }
            currentDay++;
            key++;
        }

        return dateList;
    }

    function getDay(date, day, key) {

        return (
            <div className="day" key={key}>
                <span className="day__date">{date}</span>
                <span className="title">{day}</span>
            </div>
        );
    }


    return (
        <>{formDaysList().map(day => day)}
            <Slider Slider {...settings}>
            </Slider>
        </>
    );
}//
