import React from "react";
import Slider from "react-slick";
import { sliderData } from "../../mockData/movieData";

import "./slick.scss";
import "./slick-theme.scss";
import "../Slider/Slider.scss";

export default function MovieSlider(props) {
    const settings = {
        infinite: true,
        speed: 800,
        slidesToShow: 3,
        slidesToScroll: 1,
        useTransform: false,
        variableWidth: true
    };

    function showDetails(idMovie) {
        props.getAboutMovie(idMovie);
    }

    function getSlides() {
        return sliderData.map(({ src, title, id }) => (
            <a className="slide" key={id} onClick={() => showDetails(id)}>
                <div className="slide__id">{(id < 10 ? "0" + id : id)} </div>
                <div style={{ backgroundImage: `url('${src}')` }} className="slide__image slide-image">
                    <span className="slide__title"> {title} </span>
                </div>
            </a>
        ));
    }

    return (
        <Slider {...settings}>
            {getSlides()}
        </Slider>
    );
}
