import React, { useState } from "react";
import { HeaderTop } from "../Header/Header";
import Footer from "../Footer/Footer";
import { Link } from "react-router-dom";
import "../Registration/Registration.scss";

export default function Registration() {
    const [openTab, setTab] = useState(true);

    function openChosenTab(isPersonal) {
        setTab(isPersonal);
    }

    return (
        <>
            <header className="header header--regist">
                <div className="header__container">
                    <HeaderTop tabs="true" openTab={openChosenTab} />
                </div>
            </header>
            <main className="main">
                <div className="main__history history">
                    <Link className="history__link" to="/">Головна</Link> /
                    <Link className="history__link" to="/">Реєстрація</Link>
                </div>
                <div className="main__registration registration">
                    <section className={"registration__personal personal" + (openTab ? " personal--show" : "")}>
                        <form className="form" method="POST" encType="multipart/form-data">

                        </form>
                    </section>
                    <section className={"registration__contacts contacts" + (!openTab ? " contact--show" : "")}>
                        Контакты
                    </section>
                </div>
            </main>
            <Footer />
        </>
    );
}
